local PANEL = {}function PANEL:Init ()	Gooey.EventProvider (self)end-- Eventsfunction PANEL:DoClick ()	self:DispatchEvent ("Click")endfunction PANEL:DoRightClick ()	self:DispatchEvent ("RightClick")endfunction PANEL:OnMousePressed (mouseCode)	self:DispatchEvent ("MouseDown", mouseCode, self:CursorPos ())	DButton.OnMousePressed (self, mouseCode)endfunction PANEL:OnMouseReleased (mouseCode)	self:DispatchEvent ("MouseUp", mouseCode, self:CursorPos ())	DButton.OnMouseReleased (self, mouseCode)endvgui.Register ("GButton", PANEL, "DButton")